import React, { useState } from 'react';
import axios from 'axios';

API_KEY = 'c4b563ce29cb3e9a9ec3703b9ed3cc64'

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  const fetchWeather = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:5000/api/weather?lat=${latitude}&lon=${longitude}&appid={}`);
      setWeather(response.data);
      setError(null);
    } catch (err) {
      // Check if error response exists to provide a more detailed error message
      if (err.response) {
        setError(`Error: ${err.response.data.error}`);
      } else {
        setError('Failed to fetch weather data');
      }
      setWeather(null);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchWeather();
  };

  return (
    <div className="App">
      <h1>Weather App</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={latitude}
          onChange={(e) => setLatitude(e.target.value)}
          placeholder="Enter latitude"
          required
        />
        <input
          type="text"
          value={longitude}
          onChange={(e) => setLongitude(e.target.value)}
          placeholder="Enter longitude"
          required
        />
        <button type="submit">Get Weather</button>
      </form>
      {error && <p>{error}</p>}
      {weather && (
        <div>
          <h2>Weather</h2>
          <p>Temperature: {weather.temperature}Â°C</p>
          <p>Description: {weather.description}</p>
          <p>Humidity: {weather.humidity}%</p>
          <p>Wind Speed: {weather.wind_speed} m/s</p>
        </div>
      )}
    </div>
  );
}

export default App;
