import React, { useState } from 'react';

const WeatherApp = () => {
    const [cityName, setCityName] = useState('');
    const [weatherInfo, setWeatherInfo] = useState(null);
    const [error, setError] = useState(null);
    const apiKey = 'c4b563ce29cb3e9a9ec3703b9ed3cc64'; // Replace with your OpenWeather API key
    const [weekdayweatherInfo, setweekdayWeatherInfo] = useState(null);
    const getWeather = async () => {

        const url = `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${apiKey}&units=metric`;

        try {
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error("City not found");
            }
            const data = await response.json();
            setWeatherInfo(data);
            setError(null);
        } catch (err) {
            setError(err.message);
            setWeatherInfo(null);
        }
    };

    const getWeekdaysWeathers = async () => {
      // const apiKey = 'c4b563ce29cb3e9a9ec3703b9ed3cc64'; // Replace with your OpenWeather API key
      const url = `api.openweathermap.org/data/2.5/forecast/daily?q={city name}&cnt={cnt}&appid={API key}`;

      try {
          const response = await fetch(url);
          if (!response.ok) {
              throw new Error("City not found");
          }
          const data = await response.json();
          setWeatherInfo(data);
          setError(null);
      } catch (err) {
          setError(err.message);
          setWeatherInfo(null);
      }
  };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <h1>Weather Information</h1>
            <input
                type="text"
                placeholder="Enter city name"
                value={cityName}
                onChange={(e) => setCityName(e.target.value)}
            />
            <button onClick={getWeather}>Get Weather</button>
            <button onClick={getWeekdaysWeathers}>Get 5 Day Weathers</button>

            <div id="weather-info" style={{ marginTop: '20px' }}>
                {error && <p>{error}</p >}
                {weatherInfo && (
                    <div>
                        <h2>{weatherInfo.name}, {weatherInfo.sys.country}</h2>
                        <p>Temperature: {weatherInfo.main.temp} °C</p >
                        <p>Weather: {weatherInfo.weather[0].description}</p >
                        <p>Humidity: {weatherInfo.main.humidity} %</p >
                        <p>Wind Speed: {weatherInfo.wind.speed} m/s</p >
                    </div>
                )}
            </div>
        </div>
    );
};

export default WeatherApp;


